import { Dropdown, Image, Input, Menu, message, Modal } from "antd";
import type { MenuInfo } from "rc-menu/lib/interface";

import * as fileApi from "@/api/fileApi";
import * as fileTypes from "@/types/file";

import style from "./index.module.less";

// 文件元素
export function getFileVNode(
  file: fileTypes.File, // 文件信息
  scaleFactor: number, // 缩放系数
  isSelected: boolean, // 是否选中
  setSelectedFiles: (files: fileTypes.File[]) => void, // 设置选中
  getFiles: () => void // 重新获取文件
): JSX.Element {
  const width = `${scaleFactor * 100}%`;

  const imgClassName = isSelected
    ? `${style["file-list__file-img"]} ${style["file-list__file-img--selected"]}`
    : `${style["file-list__file-img"]}`;
  const nameClassName = isSelected
    ? `${style["file-list__file-name"]} ${style["file-list__file-name--selected"]}`
    : `${style["file-list__file-name"]}`;

  return (
    <Dropdown
      overlay={getFileOptionVNode(file, getFiles)}
      trigger={["contextMenu"]}
      key={file.id}
      onOpenChange={(e) => e && setSelectedFiles([file])}
    >
      <div
        className={style["file-list__file"]}
        style={{
          width,
          height: width,
        }}
        onClick={() => setSelectedFiles([file])}
      >
        <div className={imgClassName}>
          <Image
            width="100%"
            height="100%"
            src={file.content}
            fallback="data:image/png;base64,"
          />
        </div>
        <div className={nameClassName}>{file.name}</div>
      </div>
    </Dropdown>
  );
}

// 文件右键元素
function getFileOptionVNode(file: fileTypes.File, getFiles: () => void) {
  const items = [
    {
      label: "重命名",
      key: "rename",
    },
    {
      label: "删除",
      key: "remove",
    },
  ];

  async function onClick(e: MenuInfo) {
    const options: any = {
      rename: fileRename,
      remove: fileRemove,
    };

    await options[e.key](file);

    getFiles();
  }

  return <Menu items={items} onClick={onClick} />;
}

// 文件重命名
async function fileRename(file: fileTypes.File) {
  let value = file.name;

  return new Promise((fileRenameRes) => {
    Modal.confirm({
      icon: false,
      title: "文件重命名",
      content: (
        <Input
          placeholder="请输入名称"
          defaultValue={value}
          onChange={(e) => (value = e.target.value)}
        />
      ),
      onOk() {
        return new Promise((res, rej) => {
          fileApi
            .setFiles(file.id, {
              name: value,
            })
            .then(() => {
              res(null);
              fileRenameRes(null);
            })
            .catch((err) => {
              message.error(err.response.data)
              rej();
            });
        });
      },
    });
  });
}

// 文件删除
async function fileRemove(file: fileTypes.File) {
  return new Promise((fileRenameRes) => {
    Modal.confirm({
      icon: false,
      title: "确认删除？",
      onOk() {
        return new Promise((res, rej) => {
          fileApi
            .removeFiles(file.id)
            .then(() => {
              res(null);
              fileRenameRes(null);
            })
            .catch(() => {
              rej();
            });
        });
      },
    });
  });
}
